mapper.select:
select

from app_role

	1=1
	
		and t.ROLE_ID = #{roleId}
	
	
		and t.ROLE_NAME = #{roleName}
	
	
		and t.VALID_FLAG = #{validFlag}
	


	limit #{iDisplayStart},#{iDisplayLength}

select count(1)
FROM APP_ROLE t

	1=1
	
		and t.ROLE_ID = #{roleId}
	
	
		and t.ROLE_NAME = #{roleName}
	
	
		and t.VALID_FLAG = #{validFlag}
