mapper.select:
select

from t_project

	
		AND project_id = #{projectId,jdbcType=INTEGER}
	
	
		AND project_code = #{projectCode,jdbcType=VARCHAR}
	
	
		AND project_name = #{projectName,jdbcType=VARCHAR}
	
	
		AND account_id = #{accountId,jdbcType=INTEGER}
	
	
		AND auth_id = #{authId,jdbcType=INTEGER}
	
	
		AND data_source_id = #{dataSourceId,jdbcType=INTEGER}
	
	
		AND valid_flag = #{validFlag,jdbcType=CHAR}
	
	
		AND is_default = #{isDefault,jdbcType=CHAR}
	
	
		AND created_date = #{createdDate,jdbcType=TIMESTAMP}
	
	
		AND created_user = #{createdUser,jdbcType=VARCHAR}
	
	
		AND updated_date = #{updatedDate,jdbcType=TIMESTAMP}
	
	
		AND updated_user = #{updatedUser,jdbcType=VARCHAR}
