mapper.select:
select

from t_server

	
		AND server_id = #{serverId,jdbcType=INTEGER}
	
	
		AND server_type = #{serverType,jdbcType=VARCHAR}
	
	
		AND server_version = #{serverVersion,jdbcType=VARCHAR}
	
	
		AND server_env = #{serverEnv,jdbcType=VARCHAR}
	
	
		AND server_status = #{serverStatus,jdbcType=VARCHAR}
	
	
		AND system_name = #{systemName,jdbcType=VARCHAR}
	
	
		AND server_url = #{serverUrl,jdbcType=VARCHAR}
	
	
		AND valid_flag = #{validFlag,jdbcType=CHAR}
	
	
		AND created_date = #{createdDate,jdbcType=TIMESTAMP}
	
	
		AND created_user = #{createdUser,jdbcType=VARCHAR}
	
	
		AND updated_date = #{updatedDate,jdbcType=TIMESTAMP}
	
	
		AND updated_user = #{updatedUser,jdbcType=VARCHAR}

select

from t_server

	
		AND server_id = #{serverId}
	
	
		AND server_type=#{serverType}
	
	
		AND server_version LIKE CONCAT('%',
		#{serverVersion}, '%')
	
	
		AND server_env = #{serverEnv}
	
	
		AND server_status = #{serverStatus}
	
	
		AND server_url LIKE CONCAT('%',
		#{serverUrl}, '%')
	
	
		AND valid_flag = #{validFlag}
